      out.write("          <li class=\"\"><a href=\"javascript:goToMenu('mem_binfo2')\">헌혈이란?</a></li>\r\n");
      out.write("          <li class=\"\"><a href=\"javascript:goToMenu('mem_binfo3')\">헌혈과정</a></li>\r\n");
      out.write("        </ul>\r\n");
